package com.network;

public class Taskswuthswitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 42;
		String size = null;
		
		//switch statement to check size
		switch (number) {
		case 38:
			size = "XS";
			break;
		case 40:
			size ="S";
			break;
		case 42:
			size = "M";
			break;
		case 44:
			size = "L";
			
		}
		System.out.println("SIZE: " + size);
	}

}
