package com.network;

public class largestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 12, b = 25; 
		int max = 0;
		
		 max = (a > b) ? a: b;
		System.out.println("Maximum is: " + max);
		
		int n1 = 12;
		int n2 = 23;
		int result = 0;
		
		 result = (n1 > n2) ? (n1 + n2) : (n1 - n2);
		System.out.println("Result is: "  + result);
		
	}

}
