package com.chika;

public class JavaPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("   J   a  v    v  a");
		System.out.println("   J  a a  v  v  a a");
		System.out.println("J  J aaaaa  V V aaaaa");
		System.out.println(" JJ  a    a  V  a   a");
		System.out.println("======================");
		System.out.println(((25.5*3.5-3.5*3.5)/(40.5-4.5)));
		System.out.println("=======================");
		System.out.println(4.0 * (1 - (1.0/3) + (1.0/5) - (1.0/7) + (1.0/9) - (1.0/11)));
		System.out.println("=======================");
		
		double radius = 7.5;
		double area = Math.PI*(radius*radius);
		System.out.println("The area of a circle is: " + area);
		double perimeter = 2*Math.PI*radius;
		System.out.println("Perimeter os a circle is: " + perimeter);
		System.out.println("==================================");
		
		double width = 5.6;
		double height = 8.5;
		double area2 = width*height;
		double perimeter2 = 2 * (5.6+8.5);
		System.out.println("Area is: 5.6*8.5 = " +area2);
		System.out.println("Perimeter is: 2*(5.6+8.5) =  " + perimeter2);
		System.out.printf("Area is %.1f *%.1f =%.2f\n",width,height,area2);
		System.out.printf("The perimeter is 2*(%.1f + %.1f) =%.2f\n",width,height,perimeter2);
		
		System.out.println("* * * * * * ==========================");
		System.out.println(" * * * * *  ==========================");
		System.out.println("* * * * * * ==========================");
		System.out.println(" * * * * *  ==========================");
		System.out.println("* * * * * * ==========================");
		System.out.println(" * * * * *  ==========================");
		System.out.println("* * * * * * ==========================");
		System.out.println(" * * * * *  ==========================");
		System.out.println("* * * * * * ==========================");
		System.out.println("======================================");
		System.out.println("======================================");
		System.out.println("======================================");
		System.out.println("======================================");
		System.out.println("======================================");

		
	}

}
